import React, { useEffect, useState } from 'react';
import axios from 'axios';
import companyList from './modules/comanyList.js';
import Tooltip from './tooltip.js';
import './App.css';
import 'antd/dist/antd.css'
import { AutoComplete } from 'antd';
import Chart from 'react-google-charts';

const base = 'https://cloud.iexapis.com/stable/stock';
const token = '/chart/1m?token=pk_0b61d9bd2072480cb885c51c1a47f59d';
const generateUrl = (code) => `${base}/${code}${token}`;

const tooltipStyle = '"width: 150px; padding: 10px"';

const App = () => {
  const [search, setSearch] = useState('');
  const [options, setOptions] = useState([]);
  const [history, setHistory] = useState([]);
  const [stockName, setStockName] = useState('');

  useEffect(() => {
    let matches = [];
    if (search.length) {
      matches = companyList.filter(
        company => company.lowerName.includes(search) ||
        company.lowerSymbol.includes(search)
      );
    }

    setOptions(matches);
  }, [search])

  const onSearch = (value) => {
    const searchInput = value.toLowerCase();
    setSearch(searchInput);
  };

  const onSelect = value => setSearch(value);

  const checkStock = async () => {
    const resp = await axios.get(generateUrl(search));
    setHistory(parseData(resp.data));
    const name = companyList.find(company => company.lowerSymbol === search).name;
    setStockName(name);
  }

  const createTooltip = ({ date, high, low, open, close }) => (
    `<div style=${tooltipStyle}>
      <div>
        ${date}
      </div>
      <div>
        Open - ${open}
      </div>
      <div>
        Close - ${close}
      </div>
      <div>
        Low - ${low}
      </div>
      <div>
        High - ${high}
      </div>
    </div>`
  );

  const parseData = (stocks) => {

    return stocks.map((stock) => {
      console.log(createTooltip(stock))
      return [stock.date, stock.low, stock.close, stock.open, stock.high, React.renderToString(<Tooltip stock={stock} />)]
      // return [stock.date, stock.low, stock.close, stock.open, stock.high, createTooltip(stock)]
    })
  }

  return (
    <div className='App'>
      <AutoComplete
        onSearch={onSearch}
        value={search}
        style={{ width: '300px' }}
        onSelect={onSelect}
      >
        {options.map(option => (
          <AutoComplete.Option key={option.lowerSymbol} value={option.lowerSymbol}>
            {`${option.symbol} - ${option.name}`}
          </AutoComplete.Option>
        ))}
      </AutoComplete>
      <button onClick={checkStock}>Check Stock</button>
      {history.length && <Chart
        width={'100%'}
        height={350}
        chartType='CandlestickChart'
        loader={<div>Loading Chart</div>}
        data={[
          [
            { type: 'string', id: 'Date' },
            { type: 'number', label: 'Low' },
            { type: 'number', label: 'Close' },
            { type: 'number', label: 'Open' },
            { type: 'number', label: 'High'  },
            { type: 'string', label: 'Tooltip', role: 'tooltip', p: { html: true } },
          ],
          ...history,
        ]}
        options={{
          legend: 'none',
          candlestick: {
            fallingColor: { strokeWidth: 0, fill: '#a52714' },
            risingColor: { strokeWidth: 0, fill: '#0f9d58' },
          },
          hAxis: {
            title: 'Date',
            titleTextStyle: {
              bold: true,
              italic: false,
            },
          },
          title: stockName,
          titleTextStyle: {
            bold: true,
            fontSize: 24,
          },
          tooltip: {
            isHtml: true,
          },
          vAxis: {
            title: 'Price',
            titleTextStyle: {
              bold: true,
              italic: false,
            },
          },
        }}
        rootProps={{ 'data-testid': '1' }}
      />}
    </div>
  );
}

export default App;
