import React, { useEffect, useState } from 'react';
import axios from 'axios';
import companyList from './modules/comanyList.js';
import './App.css';
import 'antd/dist/antd.css'
import { AutoComplete } from 'antd';
import CanvasJSReact from './canvasjs.react';
//var CanvasJSReact = require('./canvasjs.react');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const base = 'https://cloud.iexapis.com/stable/stock';
const token = '/chart/1m?token=pk_0b61d9bd2072480cb885c51c1a47f59d';
const generateUrl = (code) => `${base}/${code}${token}`;

const getDailyAvgs = ({ data }) => (
  data.map(({ open, close, high, low }) => (
    parseFloat(((open + close + high + low) / 4).toFixed(2))
  ))
);

const App = () => {
  const [search, setSearch] = useState('');
  const [options, setOptions] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    let matches = [];
    if (search.length) {
      matches = companyList.filter(
        company => company.lowerName.includes(search) ||
        company.lowerSymbol.includes(search)
      );
    }

    setOptions(matches);
  }, [search])

  const onSearch = (value) => {
    const searchInput = value.toLowerCase();
    setSearch(searchInput);
    // setDailyAvgs([]);
  };

  const onSelect = value => setSearch(value);

  const checkStock = async () => {
    const resp = await axios.get(generateUrl(search));
    debugger
    // setDailyAvgs(getDailyAvgs(resp));
    // console.log(getDailyAvgs(resp));
  }

  const chartOptions = {
			theme: "light2", // "light1", "light2", "dark1", "dark2"
			animationEnabled: true,
			exportEnabled: true,
			title:{
				text: "Intel Corporation Stock Price -  2017"
			},
			axisX: {
				valueFormatString: "MMM"
			},
			axisY: {
				includeZero:false,
				prefix: "$",
				title: "Price (in USD)"
			},
			data: [{
				type: "candlestick",
				showInLegend: true,
				name: "Intel Corporation",
				yValueFormatString: "$###0.00",
				xValueFormatString: "MMMM YY",
				dataPoints: [stocks]
      }],
    };

  return (
    <div className="App">
      <AutoComplete
        onSearch={onSearch}
        value={search}
        style={{ width: '300px' }}
        onSelect={onSelect}
      >
        {options.map(option => (
          <AutoComplete.Option key={option.lowerSymbol} value={option.lowerSymbol}>
            {`${option.symbol} - ${option.name}`}
          </AutoComplete.Option>
        ))}
      </AutoComplete>
      <button onClick={checkStock}>Check Stock</button>
    </div>
  );
}

export default App;
