import React, { useEffect, useState } from 'react';
import axios from 'axios';
import companyList from './modules/comanyList.js';
import './App.css';
import 'antd/dist/antd.css'
import { AutoComplete } from 'antd';
import Chart from 'react-google-charts';

const base = 'https://cloud.iexapis.com/stable/stock';
const token = '/chart/1m?token=pk_0b61d9bd2072480cb885c51c1a47f59d';
const generateUrl = (code) => `${base}/${code}${token}`;

const getDailyAvgs = ({ data }) => (
  data.map(({ open, close, high, low }) => (
    parseFloat(((open + close + high + low) / 4).toFixed(2))
  ))
);

const App = () => {
  const [search, setSearch] = useState('');
  const [options, setOptions] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    let matches = [];
    if (search.length) {
      matches = companyList.filter(
        company => company.lowerName.includes(search) ||
        company.lowerSymbol.includes(search)
      );
    }

    setOptions(matches);
  }, [search])

  const onSearch = (value) => {
    const searchInput = value.toLowerCase();
    setSearch(searchInput);
  };

  const onSelect = value => setSearch(value);

  const checkStock = async () => {
    const resp = await axios.get(generateUrl(search));
    debugger
    // setDailyAvgs(getDailyAvgs(resp));
    // console.log(getDailyAvgs(resp));
  }



  return (
    <div className="App">
      <AutoComplete
        onSearch={onSearch}
        value={search}
        style={{ width: '300px' }}
        onSelect={onSelect}
      >
        {options.map(option => (
          <AutoComplete.Option key={option.lowerSymbol} value={option.lowerSymbol}>
            {`${option.symbol} - ${option.name}`}
          </AutoComplete.Option>
        ))}
      </AutoComplete>
      <button onClick={checkStock}>Check Stock</button>
      {<Chart
        width={'100%'}
        height={350}
        chartType="CandlestickChart"
        loader={<div>Loading Chart</div>}
        data={[
          ['day', 'a', 'b', 'c', 'd'],
          ['Mon', 20, 28, 38, 45],
          ['Tue', 31, 38, 55, 66],
          ['Wed', 50, 55, 77, 80],
          ['Thu', 77, 77, 66, 50],
          ['Fri', 68, 66, 22, 15],
        ]}
        options={{
          legend: 'none',
        }}
        rootProps={{ 'data-testid': '1' }}
      />}
    </div>
  );
}

export default App;
